mapper.select:


	SELECT * 
	  FROM (SELECT A.*,
                   N_Board_Num AS rn 
	 		  FROM (SELECT *
					  FROM mvc_Notice_TBL
 					 WHERE show = 'y'
 					 ORDER BY N_Board_Num DESC) A 
 			)
	 WHERE rn >= #{start}
	   AND rn <= #{end}

mapper.update:
UPDATE mvc_Notice_TBL 
   SET N_readCnt = N_readCnt+1 
 WHERE N_Board_Num= #{n_Board_Num}

UPDATE mvc_Notice_TBL 
   SET N_title = #{n_Title}
      ,N_content = #{n_Content}
 WHERE N_Board_Num= #{n_Board_Num}

UPDATE mvc_Notice_TBL 
   SET show = 'n'
 WHERE N_Board_Num= #{n_Board_Num}
